// Security validation and lead detection
const crypto = require('crypto');

// Verify webhook signature
function verifyWebhookSignature(payload, signature, secret) {
  if (!secret || !signature) return false;

  try {
    const expectedSignature = crypto
      .createHmac('sha256', secret)
      .update(JSON.stringify(payload), 'utf8')
      .digest('hex');

    const receivedSignature = signature.replace('sha256=', '');

    return crypto.timingSafeEqual(
      Buffer.from(expectedSignature, 'hex'),
      Buffer.from(receivedSignature, 'hex')
    );
  } catch (error) {
    return false;
  }
}

// Security check
const webhookSecret = $env.ECOMMERCE_WEBHOOK_SECRET;
const signature = $node["Webhook Trigger"].json["headers"]["x-webhook-signature"];

if (!verifyWebhookSignature($json, signature, webhookSecret)) {
  throw new Error('Invalid webhook signature');
}

// Validate payload
if (!$json.sessionId || !$json.messageId || !$json.message?.content) {
  throw new Error('Invalid payload structure');
}

// Determine if this is a potential lead
function isLeadCandidate(payload) {
  // Guest users are potential leads
  if (!payload.userContext.isAuthenticated && payload.guestEmail) {
    return true;
  }

  // Users asking about products without recent purchases
  if (payload.userContext.isAuthenticated && 
      (!payload.userContext.membershipTotal || payload.userContext.membershipTotal < 50)) {
    return true;
  }

  return false;
}

// Detect lead intent
function detectLeadIntent(message) {
  const content = message.toLowerCase();

  const intents = {
    product_interest: ['interested', 'want to buy', 'looking for', 'need', 'searching', 'price'],
    comparison_shopping: ['compare', 'vs', 'versus', 'better than', 'which is', 'difference'],
    discount_seeking: ['discount', 'coupon', 'deal', 'sale', 'promo', 'cheaper', 'save money'],
    first_time_buyer: ['first time', 'new customer', 'never bought', 'trying', 'considering'],
    high_intent: ['buy now', 'purchase', 'order', 'checkout', 'add to cart', 'how to buy']
  };

  for (const [intent, keywords] of Object.entries(intents)) {
    if (keywords.some(keyword => content.includes(keyword))) {
      return intent;
    }
  }

  return 'general_inquiry';
}

// Add lead analysis to payload
$json.leadAnalysis = {
  isLead: isLeadCandidate($json),
  intent: detectLeadIntent($json.message.content),
  source: $json.sessionMetadata?.source || 'chat',
  timestamp: new Date().toISOString(),
  sessionId: $json.sessionId
};

return $json;

// High-intent lead response - immediate sales focus
const userMessage = $json.message.content;
const guestEmail = $json.guestEmail;
const userName = $json.userContext.userInfo?.name || 'there';
const isAuthenticated = $json.userContext.isAuthenticated;

let responseContent = '';
let quickReplies = [];

if (!isAuthenticated) {
  responseContent = `I can see you're interested in making a purchase! I'd love to help you complete your order. Creating an account takes just 30 seconds and gives you:\n\nâ€¢ Exclusive member discounts (10-20% off)\nâ€¢ Free shipping on orders over $50\nâ€¢ Priority customer support\nâ€¢ Order tracking\n\nShould I help you get started with your purchase?`;
  quickReplies = ['Create Account & Buy', 'Continue as Guest', 'See Discounts', 'Speak to Sales'];
} else {
  responseContent = `Hi ${userName}! I can see you're ready to make a purchase. As a member, you have access to exclusive discounts and free shipping. Let me help you complete your order quickly!`;
  quickReplies = ['Show Products', 'Apply Discount', 'Quick Checkout', 'Speak to Sales'];
}

return {
  sessionId: $json.sessionId,
  response: {
    content: responseContent,
    type: 'quick_reply',
    quickReplies: quickReplies
  },
  metadata: {
    intent: 'high_purchase_intent',
    leadScore: 95,
    urgency: 'high',
    followUpRequired: true,
    salesQualified: true
  }
};

// Regular lead nurturing response
const userMessage = $json.message.content;
const leadIntent = $json.leadAnalysis.intent;
const guestEmail = $json.guestEmail;
const userName = $json.userContext.userInfo?.name || 'there';
const isAuthenticated = $json.userContext.isAuthenticated;

let responseContent = '';
let quickReplies = [];
let leadScore = 50;

// Customize response based on lead intent
switch (leadIntent) {
  case 'product_interest':
    responseContent = `Hi ${userName}! I'd be happy to help you find the perfect product. What specific features are you looking for? I can show you our top-rated items and current deals.`;
    quickReplies = ['Browse Products', 'See Top Rated', 'Current Deals', 'Get Recommendations'];
    leadScore = 70;
    break;

  case 'comparison_shopping':
    responseContent = `Great question! I can help you compare our products to find the best fit for your needs. We also offer a price match guarantee and free returns.`;
    quickReplies = ['Compare Products', 'Price Match', 'See Reviews', 'Get Expert Advice'];
    leadScore = 75;
    break;

  case 'discount_seeking':
    responseContent = `You're in luck! We have several ways to save:\n\nâ€¢ New customer discount: 15% off first order\nâ€¢ Member exclusive deals up to 30% off\nâ€¢ Free shipping on orders $50+\nâ€¢ Bundle discounts\n\nWant to see what's available?`;
    quickReplies = ['New Customer Deal', 'See All Deals', 'Create Account', 'Bundle Offers'];
    leadScore = 80;
    break;

  case 'first_time_buyer':
    responseContent = `Welcome! It's great to have you here. As a first-time customer, I can offer you:\n\nâ€¢ 15% off your first order\nâ€¢ Free shipping\nâ€¢ 30-day money-back guarantee\nâ€¢ Personal shopping assistance\n\nWhat can I help you find today?`;
    quickReplies = ['Claim First-Time Deal', 'Browse Catalog', 'Popular Items', 'Get Help Shopping'];
    leadScore = 85;
    break;

  default:
    if (!isAuthenticated) {
      responseContent = `Hi ${userName}! Thanks for your interest. I'm here to help you find exactly what you need. Creating a free account gives you access to member prices and exclusive deals.`;
      quickReplies = ['Browse Products', 'Create Account', 'See Deals', 'Need Help'];
    } else {
      responseContent = `Hi ${userName}! How can I help you today? As a member, you have access to exclusive deals and personalized recommendations.`;
      quickReplies = ['Member Deals', 'Recommendations', 'Browse Products', 'Account Settings'];
    }
    leadScore = 60;
}

return {
  sessionId: $json.sessionId,
  response: {
    content: responseContent,
    type: 'quick_reply',
    quickReplies: quickReplies
  },
  metadata: {
    intent: leadIntent,
    leadScore: leadScore,
    urgency: 'medium',
    followUpRequired: true,
    salesQualified: leadScore >= 70
  }
};

// Non-lead standard response
const userMessage = $json.message.content.toLowerCase();
const userName = $json.userContext.userInfo?.name || 'there';
const isAuthenticated = $json.userContext.isAuthenticated;

let responseContent = '';
let quickReplies = [];

// Standard helpful response for existing customers
if (userMessage.includes('order') || userMessage.includes('tracking')) {
  responseContent = `Hi ${userName}! I can help you with your order. What would you like to know?`;
  quickReplies = ['Track Order', 'Order History', 'Return Item', 'Contact Support'];
} else if (userMessage.includes('account') || userMessage.includes('profile')) {
  responseContent = `Hi ${userName}! I can help with your account settings and profile information.`;
  quickReplies = ['Account Settings', 'Update Profile', 'View Orders', 'Contact Support'];
} else if (userMessage.includes('help') || userMessage.includes('support')) {
  responseContent = `Hi ${userName}! I'm here to help. What can I assist you with today?`;
  quickReplies = ['Order Help', 'Product Info', 'Account Help', 'Technical Support'];
} else {
  responseContent = `Hello ${userName}! Thanks for reaching out. How can I assist you today?`;
  quickReplies = ['Browse Products', 'Check Orders', 'Account Help', 'Contact Support'];
}

return {
  sessionId: $json.sessionId,
  response: {
    content: responseContent,
    type: 'quick_reply',
    quickReplies: quickReplies
  },
  metadata: {
    intent: 'customer_service',
    leadScore: 10,
    urgency: 'normal',
    followUpRequired: false,
    salesQualified: false
  }
};

